//只需要把 mgyy 批量替换成你想取的名字
// 把  咪咕音乐  改成软件名  

const $ = new Env('咪咕音乐');
let status;

status = (status = ($.getval("mgyystatus") || "1")) > 1 ? `${status}` : "";

const mgyyurlArr = [], mgyyhdArr = [], mgyycount = ''

let mgyyurl = $.getdata('mgyyurl')
let mgyyhd = $.getdata('mgyyhd')




!(async () => {
    if (typeof $request !== "undefined") {

        mgyyck()

    } else {
        mgyyurlArr.push($.getdata('mgyyurl'))
        mgyyhdArr.push($.getdata('mgyyhd'))


        let mgyycount = ($.getval('mgyycount') || '1');

        for (let i = 2; i <= mgyycount; i++) {

            mgyyurlArr.push($.getdata(`mgyyurl${i}`))
            mgyyhdArr.push($.getdata(`mgyyhd${i}`))


        }

        console.log(
            `\n\n=============================================== 脚本执行 - 北京时间(UTC+8)：${new Date(
                new Date().getTime() +
                new Date().getTimezoneOffset() * 60 * 1000 +
                8 * 60 * 60 * 1000
            ).toLocaleString()} ===============================================\n`);

        for (let i = 0; i < mgyyhdArr.length; i++) {

            if (mgyyhdArr[i]) {

                mgyyurl = mgyyurlArr[i];
                mgyyhd = mgyyhdArr[i];


                $.index = i + 1;
                console.log(`\n\n开始【咪咕音乐${$.index}】`)

                //循环运行
                for (let c = 0; c < 10; c++) {
                    $.index = c + 1
                    console.log(`\n第${c+1}次执行任务！\n等待1秒开始下一次任务`)

                    await mgyyfx()//你要执行的版块  
                    await $.wait(1689)//你要延迟的时间  1000=1秒




                }
            }
        }
    }
})()

    .catch((e) => $.logErr(e))
    .finally(() => $.done())


//获取ck
function mgyyck() {
    if ($request.url.indexOf("share_report.do") > -1) {
        const mgyyurl = $request.url
        if (mgyyurl) $.setdata(mgyyurl, `mgyyurl${status}`)
        $.log(mgyyurl)

        const mgyyhd = $request.hearers
        if (mgyyhd) $.setdata(mgyyhd, `mgyyhd${status}`)
        $.log(mgyyhd)

        $.msg($.name, "", `咪咕音乐${status}获取headers成功`)

    }
}




//版块 取名字：咪咕音乐分享任务
function mgyyfx(timeout = 0) {
    return new Promise((resolve) => {

        let url = {
            url: `https://app.u.nf.migu.cn/MIGUM2.0/v1.0/user/share_report.do?bgUrl=https%3A//d.musicapp.migu.cn/prod/file-service/file-down/8121e8df41a5c12f48b69aea89b71dab/ab4e83ef6af4a3e12bc6c1a95a44d30e/5a8534959a19b6c19aaceefa50894be2&des=%E8%94%A1%E4%BE%9D%E6%9E%97&ownerId=99&ownerName=%E8%94%A1%E4%BE%9D%E6%9E%97&platform=3&resourceId=600919000005351409&resourceName=%E6%97%A5%E4%B8%8D%E8%90%BD%28%E7%94%B5%E5%BD%B1%E3%80%8A%E8%AE%B0%E5%BF%86%E7%9A%84%E8%A3%82%E7%97%95%E3%80%8B%E6%8F%92%E6%9B%B2%29&shareUrl=http%3A//c.migu.cn/00dlnc?ifrom%3D8979ed0a69879b200d011c86dcb8d574&type=2`,
            headers: {'bversionid' : `DF94879092A1A28A6B9A89A0D0ADA076989C8890979DD891629784A1B6AF9B6EC4C8888F8ED6D48C6A91B8D08D7C9EA29BDFD0D3DDA5A58967A28CB38776A985A8A8818D93A0B48578A59DA6817FA974939B898793B1AA9B66DD879D847997719393818791A0A2856291879D817997719390848791A0A2886291879D8479E3729998858C97A0A48B63978D9F`,
'User-Agent' : `MGMobileMusic/7.9.1 (iPhone; iOS 14.8.1; Scale/3.00)`} ,
        }

        $.get(url, async (err, resp, data) => {
            try {

                data = JSON.parse(data)

                if (data.code == 000000) {
        console.log('分享任务：成功🎉')

                } else {

        $.log('分享任务：失败❌ '+ data.info)

                }
            } catch (e) {

            } finally {

                resolve()
            }
        }, timeout)
    })
}





